import 'package:flutter/material.dart';

class Transparentappbar extends StatefulWidget {
  const Transparentappbar({Key? key}) : super(key: key);

  @override
  _TransparentappbarState createState() => _TransparentappbarState();
}

class _TransparentappbarState extends State<Transparentappbar> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      extendBodyBehindAppBar: true,
      appBar: AppBar(
        leading: BackButton(),
        title: Text(
          'Transparent AppBar',
          style: TextStyle(fontSize: 30, fontWeight: FontWeight.bold),
        ),
        centerTitle: true,
        actions: [IconButton(onPressed: () {}, icon: Icon(Icons.search))],
        backgroundColor: Colors.transparent,
        elevation: 0,
      ),
      body: Container(
          decoration: BoxDecoration(
        image: DecorationImage(
          image: NetworkImage(
              'data:image/jpeg;base64,'),
          fit: BoxFit.cover,
        ),
      )),
    );
  }
}
